    setShowCart(true);
  }

  const hideCartHandler=()=>{
    setShowCart(false);
  }

  return (
    <CartProvider>
      {/* <Header onShowcart={showCartHandler}/>
      {showCart && <Cart onClose={hideCartHandler} />} */}
    <main>
        <Meals />
      </main>
      </CartProvider>
  );
}

export default App;
